definition.script:
pipeline {
    agent {
        label 'Slave'
    }

    tools {
        // Install the Maven version configured as "M3" and add it to the path.
        maven "m3"
    }

    stages {
        stage('Build') {
            steps {
                // Get some code from a GitHub repository
                git branch: 'seleniumgrid', url: 'https://github.com/LucekC/panda_application'

                sh "mvn clean install"

            }
        }
        stage('Test') {
            steps {

                sh "mvn test -Pselenium"

            }
        }
        stage('Deploy') {
            steps {
                configFileProvider([configFile(fileId: 'fa950e11-149a-4312-95c1-ce4cb45621fc', variable: 'mavensettings')]) {
                    // some block
                    sh "mvn -s $mavensettings deploy -Dmaven.test.skip=true -e"
                }


                // To run Maven on a Windows agent, use
                // bat "mvn -Dmaven.test.failure.ignore=true clean package"
            }

        }
    }
}
